// ================================
// REPID - Wallet Connector (injected into page)
// Dark Theme - DOM API version (no innerHTML for CSP compatibility)
// ================================

(function() {
  'use strict';
  
  // If modal function already exists, just call it and exit
  if (typeof window.__REPID_SHOW_WALLET_MODAL__ === 'function') {
    window.__REPID_SHOW_WALLET_MODAL__();
    return;
  }
  
  // Define the show modal function
  window.__REPID_SHOW_WALLET_MODAL__ = async function() {
    // Base64 encoded icons (inside function to be available on repeated calls)
    const REPID_LOGO = 'data:image/png;base64,';
    const REPID_PHANTOM = 'data:image/png;base64,';
    const REPID_METAMASK = 'data:image/png;base64,';
    
    // Wait for document.body
    if (!document.body) {
      console.log('REPID: Waiting for document.body...');
      setTimeout(() => window.__REPID_SHOW_WALLET_MODAL__(), 100);
      return;
    }
    
  try {
    // Remove existing modal if present
    const existingModal = document.getElementById('repid-wallet-modal');
    if (existingModal) {
      existingModal.remove();
    }
    
    // Clear any pending state
    window.__REPID_CONNECTING__ = false;
    
    console.log('REPID: Loading wallet connector...');
    
    // Create styles
    const style = document.createElement('style');
    style.textContent = `
      #repid-wallet-overlay {
        position: fixed;
        inset: 0;
        background: rgba(6, 6, 10, 0.85);
        backdrop-filter: blur(10px);
        -webkit-backdrop-filter: blur(10px);
        z-index: 2147483647;
        display: flex;
        align-items: center;
        justify-content: center;
        font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', system-ui, sans-serif;
        animation: repid-fade-in 0.2s ease;
      }
      @keyframes repid-fade-in {
        from { opacity: 0; }
        to { opacity: 1; }
      }
      #repid-wallet-box {
        background: #0c0c14;
        border: 1px solid rgba(74, 58, 255, 0.2);
        border-radius: 20px;
        padding: 40px 36px;
        width: 400px;
        max-width: 90vw;
        box-shadow: 0 20px 50px rgba(0, 0, 0, 0.5), 0 0 60px rgba(35, 136, 255, 0.1);
        position: relative;
        animation: repid-slide-up 0.25s ease;
      }
      @keyframes repid-slide-up {
        from { opacity: 0; transform: translateY(15px) scale(0.98); }
        to { opacity: 1; transform: translateY(0) scale(1); }
      }
      .repid-header {
        text-align: center;
        margin-bottom: 36px;
      }
      .repid-logo {
        width: 60px;
        height: 60px;
        margin: 0 auto 20px;
        display: block;
      }
      .repid-title {
        color: #F8F3FF;
        font-size: 20px;
        font-weight: 600;
        margin: 0 0 8px 0;
        letter-spacing: 1px;
        text-transform: uppercase;
      }
      .repid-subtitle {
        color: rgba(248, 243, 255, 0.4);
        font-size: 14px;
        margin: 0;
      }
      .repid-wallets {
        display: flex;
        flex-direction: column;
        gap: 16px;
      }
      .repid-wallet-btn {
        display: flex;
        align-items: center;
        gap: 18px;
        padding: 24px 24px;
        background: #14121f;
        border: 1px solid rgba(255, 255, 255, 0.1);
        border-radius: 14px;
        cursor: pointer;
        transition: all 0.2s ease;
        color: #F8F3FF;
        font-family: inherit;
        font-size: 16px;
        font-weight: 500;
        position: relative;
        min-height: 80px;
      }
      .repid-wallet-btn:hover {
        background: #1c1a2a;
        border-color: rgba(255, 255, 255, 0.2);
        transform: translateX(4px);
      }
      .repid-wallet-btn:active {
        transform: translateX(2px);
      }
      .repid-wallet-btn img {
        width: 44px;
        height: 44px;
        border-radius: 12px;
        flex-shrink: 0;
        object-fit: contain;
      }
      .repid-wallet-icon {
        width: 44px;
        height: 44px;
        border-radius: 12px;
        display: flex;
        align-items: center;
        justify-content: center;
        flex-shrink: 0;
      }
      .repid-wallet-btn span {
        flex: 1;
        text-align: left;
      }
      .repid-wallet-arrow {
        color: rgba(255, 255, 255, 0.3);
        font-size: 20px;
        transition: all 0.2s ease;
      }
      .repid-wallet-btn:hover .repid-wallet-arrow {
        color: rgba(255, 255, 255, 0.6);
        transform: translateX(4px);
      }
      .repid-wallet-btn.loading {
        opacity: 0.5;
        pointer-events: none;
      }
      .repid-powered {
        margin-top: 28px;
        padding-top: 20px;
        border-top: 1px solid rgba(255, 255, 255, 0.08);
        text-align: center;
        font-size: 12px;
        color: rgba(255, 255, 255, 0.3);
      }
      .repid-powered strong {
        color: rgba(255, 255, 255, 0.5);
        font-weight: 600;
        letter-spacing: 1px;
      }
      .repid-close {
        position: absolute;
        top: 16px;
        right: 16px;
        width: 32px;
        height: 32px;
        background: transparent;
        border: 1px solid rgba(74, 58, 255, 0.2);
        border-radius: 10px;
        color: rgba(248, 243, 255, 0.4);
        cursor: pointer;
        display: flex;
        align-items: center;
        justify-content: center;
        transition: all 0.15s ease;
        font-size: 20px;
      }
      .repid-close:hover {
        border-color: rgba(248, 243, 255, 0.3);
        color: #F8F3FF;
      }
      .repid-status {
        text-align: center;
        padding: 28px 16px;
      }
      .repid-spinner {
        width: 40px;
        height: 40px;
        border: 2px solid rgba(74, 58, 255, 0.2);
        border-top-color: #2388FF;
        border-radius: 50%;
        animation: repid-spin 0.7s linear infinite;
        margin: 0 auto 16px;
      }
      @keyframes repid-spin {
        to { transform: rotate(360deg); }
      }
      .repid-status-text {
        color: rgba(248, 243, 255, 0.6);
        font-size: 14px;
      }
      .repid-success-icon {
        width: 48px;
        height: 48px;
        background: linear-gradient(135deg, #2388FF, #4A3AFF);
        border-radius: 50%;
        display: flex;
        align-items: center;
        justify-content: center;
        margin: 0 auto 16px;
        color: white;
        font-size: 24px;
      }
      .repid-error-icon {
        width: 48px;
        height: 48px;
        background: linear-gradient(135deg, #FF4466, #FF2244);
        border-radius: 50%;
        display: flex;
        align-items: center;
        justify-content: center;
        margin: 0 auto 16px;
        color: white;
        font-size: 24px;
      }
    `;
    
    // Create modal container
    const modalContainer = document.createElement('div');
    modalContainer.id = 'repid-wallet-modal';
    modalContainer.appendChild(style);
    
    // Create overlay
    const overlay = document.createElement('div');
    overlay.id = 'repid-wallet-overlay';
    
    // Create box
    const box = document.createElement('div');
    box.id = 'repid-wallet-box';
    
    // Create close button
    const closeBtn = document.createElement('button');
    closeBtn.className = 'repid-close';
    closeBtn.id = 'repid-close-btn';
    closeBtn.textContent = '×';
    box.appendChild(closeBtn);
    
    // Create header
    const header = document.createElement('div');
    header.className = 'repid-header';
    
    // Logo - create SVG inline
    const logoImg = document.createElement('img');
    logoImg.src = REPID_LOGO;
    logoImg.style.cssText = 'width: 60px; height: 60px; margin: 0 auto 20px; display: block;';
    logoImg.alt = 'REPID';
    header.appendChild(logoImg);
    
    const title = document.createElement('h2');
    title.className = 'repid-title';
    title.textContent = 'Connect to +rep ID';
    header.appendChild(title);
    
    const subtitle = document.createElement('p');
    subtitle.className = 'repid-subtitle';
    subtitle.textContent = 'Choose your wallet';
    header.appendChild(subtitle);
    
    box.appendChild(header);
    
    // Create wallets container
    const walletsContainer = document.createElement('div');
    walletsContainer.className = 'repid-wallets';
    walletsContainer.id = 'repid-wallets';
    
    // Phantom button
    const phantomBtn = document.createElement('button');
    phantomBtn.className = 'repid-wallet-btn';
    phantomBtn.dataset.wallet = 'phantom';
    
    const phantomIcon = document.createElement('img');
    phantomIcon.src = REPID_PHANTOM;
    phantomIcon.style.cssText = 'width: 44px; height: 44px; flex-shrink: 0; object-fit: contain; border-radius: 8px;';
    phantomIcon.alt = 'Phantom';
    phantomBtn.appendChild(phantomIcon);
    
    const phantomText = document.createElement('span');
    phantomText.textContent = 'Phantom';
    phantomBtn.appendChild(phantomText);
    
    const phantomArrow = document.createElement('span');
    phantomArrow.className = 'repid-wallet-arrow';
    phantomArrow.textContent = '→';
    phantomBtn.appendChild(phantomArrow);
    
    walletsContainer.appendChild(phantomBtn);
    
    // MetaMask button
    const metamaskBtn = document.createElement('button');
    metamaskBtn.className = 'repid-wallet-btn';
    metamaskBtn.dataset.wallet = 'metamask';
    
    const metamaskIcon = document.createElement('img');
    metamaskIcon.src = REPID_METAMASK;
    metamaskIcon.style.cssText = 'width: 44px; height: 44px; flex-shrink: 0; object-fit: cover; border-radius: 8px; background: #f5841f;';
    metamaskIcon.alt = 'MetaMask';
    metamaskBtn.appendChild(metamaskIcon);
    
    const metamaskText = document.createElement('span');
    metamaskText.textContent = 'MetaMask';
    metamaskBtn.appendChild(metamaskText);
    
    const metamaskArrow = document.createElement('span');
    metamaskArrow.className = 'repid-wallet-arrow';
    metamaskArrow.textContent = '→';
    metamaskBtn.appendChild(metamaskArrow);
    
    walletsContainer.appendChild(metamaskBtn);
    
    box.appendChild(walletsContainer);
    
    // Powered by
    const powered = document.createElement('div');
    powered.className = 'repid-powered';
    powered.appendChild(document.createTextNode('Powered by '));
    const poweredStrong = document.createElement('strong');
    poweredStrong.textContent = '+rep ID';
    powered.appendChild(poweredStrong);
    box.appendChild(powered);
    
    overlay.appendChild(box);
    modalContainer.appendChild(overlay);
    
    if (!document.body) {
      console.error('REPID: Cannot append modal - document.body is null');
      return;
    }
    
    document.body.appendChild(modalContainer);
    
    // Event handlers
    function closeModal() {
      modalContainer.remove();
      window.__REPID_CONNECTING__ = false;
    }
    
    closeBtn.addEventListener('click', closeModal);
    overlay.addEventListener('click', (e) => {
      if (e.target === overlay) closeModal();
    });
    
    // Status display functions
    function showStatus(type, text) {
      walletsContainer.style.display = 'none';
      
      let statusDiv = box.querySelector('.repid-status');
      if (!statusDiv) {
        statusDiv = document.createElement('div');
        statusDiv.className = 'repid-status';
        box.insertBefore(statusDiv, powered);
      }
      
      // Clear previous content
      while (statusDiv.firstChild) {
        statusDiv.removeChild(statusDiv.firstChild);
      }
      
      if (type === 'loading') {
        const spinner = document.createElement('div');
        spinner.className = 'repid-spinner';
        statusDiv.appendChild(spinner);
      } else if (type === 'success') {
        const icon = document.createElement('div');
        icon.className = 'repid-success-icon';
        icon.textContent = '✓';
        statusDiv.appendChild(icon);
      } else if (type === 'error') {
        const icon = document.createElement('div');
        icon.className = 'repid-error-icon';
        icon.textContent = '✕';
        statusDiv.appendChild(icon);
      }
      
      const statusText = document.createElement('div');
      statusText.className = 'repid-status-text';
      statusText.textContent = text;
      statusDiv.appendChild(statusText);
    }
    
    // Wallet connection
    async function connectWallet(walletType) {
      if (window.__REPID_CONNECTING__) return;
      window.__REPID_CONNECTING__ = true;
      
      const btn = walletType === 'phantom' ? phantomBtn : metamaskBtn;
      btn.classList.add('loading');
      
      showStatus('loading', 'Connecting...');
      
      let address = null;
      let errorMsg = null;
      
      try {
        if (walletType === 'phantom') {
          if (!window.phantom?.solana) {
            errorMsg = 'Phantom not installed';
          } else {
            const resp = await window.phantom.solana.connect();
            address = resp.publicKey.toString();
          }
        } else if (walletType === 'metamask') {
          if (!window.ethereum) {
            errorMsg = 'MetaMask not installed';
          } else {
            const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
            address = accounts[0];
          }
        }
      } catch (err) {
        console.error('REPID: Wallet error:', err);
        errorMsg = err.message || 'Connection failed';
      }
      
      btn.classList.remove('loading');
      window.__REPID_CONNECTING__ = false;
      
      if (address) {
        showStatus('success', 'Connected!');
        
        // Save to localStorage
        const walletData = {
          address: address,
          walletType: walletType,
          chainId: walletType === 'phantom' ? 'solana' : 137,
          connected: true
        };
        localStorage.setItem('REPID_WALLET', JSON.stringify(walletData));
        
        // Notify extension
        window.postMessage({
          type: 'REPID_WALLET_CONNECTED',
          address: address,
          walletType: walletType,
          chainId: walletData.chainId
        }, '*');
        
        setTimeout(closeModal, 1500);
      } else {
        if (errorMsg?.includes('not installed')) {
          showStatus('error', errorMsg);
          setTimeout(closeModal, 3000);
        } else if (errorMsg?.includes('rejected') || errorMsg?.includes('denied')) {
          showStatus('error', 'Check wallet popup');
          setTimeout(closeModal, 3000);
        } else {
          showStatus('error', errorMsg || 'Connection failed');
          setTimeout(closeModal, 3000);
        }
      }
    }
    
    // Bind wallet buttons
    phantomBtn.addEventListener('click', () => connectWallet('phantom'));
    metamaskBtn.addEventListener('click', () => connectWallet('metamask'));
    
    console.log('REPID: Wallet connector ready');
    
  } catch (error) {
    console.error('REPID: Wallet connector error:', error);
  }
  };
  
  // Call the function immediately
  window.__REPID_SHOW_WALLET_MODAL__();
})();
